  color: #fff;
  -webkit-transition: color .2s;
  transition: color .2s;
}

.dark ::-webkit-input-placeholder {
  color: #a8a8a8; /* WebKit browsers */
  -webkit-transition: color .2s;
  transition: color .2s;
}
.dark ::-moz-placeholder {
  color: #a8a8a8; /* Mozilla Firefox 19+ */
  -webkit-transition: color .2s;
  transition: color .2s;
}
