  color: #a8a8a8;/* Internet Explorer 10+ */
  -webkit-transition: color .2s;
  transition: color .2s;
}
.dark ::placeholder {
  color: #a8a8a8;
  -webkit-transition: color .2s;
  transition: color .2s;
}

.fade-in-page {
  -webkit-animation: fade-in ease-in-out 1.3s both;
  -moz-animation: fade-in ease-in-out 1.3s both;
  animation: fade-in ease-in-out 1.3s both
}

@-webkit-keyframes fade-in {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}
@-moz-keyframes fade-in {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}
@keyframes fade-in {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}

/* - Clearfix
------------------------------------------------------------- */
.clearfix::before,
.clearfix::after,
.container::before,
.container::after,
.container-fluid::before,
.container-fluid::after,
.row::before,
.row::after,
.general-section::before,
.general-section::after,
.full-width-section::before,
.full-width-section::after,
.tabs::before,
.tabs::after {
  content: "";
  display: table;
  line-height: 0;
}
.clearfix::after,
.container::after,
.container-fluid::after,
.row::after,
.general-section::after,
.full-width-section::after,
.tabs-section::after {
  clear: both;
}

/* - Floating & Alignment
------------------------------------------------------------- */
.align-left {
  float: left;
  text-align: left;
}
.align-right {
  float: right;
  text-align: right;
}
.align-center {
  display: block;
  margin-left: auto;
  margin-right: auto;
  text-align: center;
}
.vertical-align {
  position: relative;
  top: 50%;
  -webkit-transform: translateY(-50%);
  transform: translateY(-50%);
}
.vertical-align-children {
  display: -webkit-flex;
  display: -moz-flex;
  display: -ms-flexbox;
  display: flex;
  align-items: center;
  -webkit-align-items: center;
  justify-content: center;
  -webkit-justify-content: center;
  flex-direction: row;
  -webkit-flex-direction: row;
}

@media only screen and (max-width: 991px) {
  .vertical-align-children {
    display: block;
  }
}

/*--------------------------------------------------------------
/** 3. Typography
--------------------------------------------------------------*/
h1, .h1, h2, .h2, h3, .h3, h4, .h4, h5, .h5, h6, .h6 {
