/*--------------------------------------------------------------
/** 4. Divider Styles
--------------------------------------------------------------*/
.heading .divider{
    position: relative;
    width: 300px;
    margin: 0 auto;
}
.divider i.icon {
    font-size: 30px;
}
.divider .icon-wrap:before,
.divider .icon-wrap:after{
    content: "";
    position: absolute;
    border-bottom: 1px solid #fff;
    width: 120px;
    height: 17px;
}
.divider .icon-wrap:before{
    left: 0px;
}
.divider .icon-wrap:after{
    right: 0px;
}

/*--------------------------------------------------------------
/** 5. Image Styles
--------------------------------------------------------------*/
img.align-left,
.wp-caption.align-left,
img.align-right,
.wp-caption.align-right {
