/*--------------------------------------------------------------
/** 10. Table Styles
--------------------------------------------------------------*/
table, th, td {
  vertical-align: middle;
}
tr {
  line-height: 2em;
}
table {
  display: table;
  margin-bottom: 25px;
  padding: 0;
  width: 100%;
}
table:last-child {
  margin-bottom: 0;
}
table thead {
  font-weight: bold;
  color: #fff;
  text-transform: none;
}
