/* - Button hover Effects5
------------------------------------------------------------- */
.btn-hvr-effects5 {
  background-color: #111;
  border: none;
  padding: 20px;
  transition: all 0.5s;
  cursor: pointer;
}
.btn-hvr-effects5 span {
  cursor: pointer;
  display: inline-block;
  position: relative;
  transition: 0.5s;
}
.btn-hvr-effects5 span:after {
  content: '\e901';
  font-family: icomoon;
  position: absolute;
  opacity: 0;
  top: 0;
  right: -20px;
  transition: 0.5s;
}
.btn-hvr-effects5:hover span {
  padding-right: 20px;
}
.btn-hvr-effects5:hover span:after {
  opacity: 1;
  right: 0;
}

/* - Button hover Effects6
------------------------------------------------------------- */
.btn-hvr-effects6 {
    display: inline-block;
    vertical-align: middle;
    -webkit-transform: perspective(1px) translateZ(0);
    transform: perspective(1px) translateZ(0);
    box-shadow: 0 0 1px rgba(0, 0, 0, 0);
    position: relative;
    background: #fff;
    border: none;
    padding: 0.6em 1em;
}
.btn-hvr-effects6:before {
    pointer-events: none;
    position: absolute;
    z-index: -1;
    content: '';
    border-style: solid;
    -webkit-transition-duration: 0.3s;
    transition-duration: 0.3s;
    -webkit-transition-property: transform;
    transition-property: transform;
    top: calc(50% - 10px);
    right: 0;
    border-width: 10px 0 10px 10px;
    border-color: transparent transparent transparent #fff;
}
.btn-hvr-effects6:hover:before, 
.btn-hvr-effects6:focus:before, 
.btn-hvr-effects6:active:before {
    -webkit-transform: translateX(10px);
    transform: translateX(10px);
}

